import React, { useEffect, useState } from 'react';
import tvJude from './TV Jude.png';
import tvJudeFond from './TV Jude cheveux derier.png';
import usbDown from './usb down.png';
import usbUp from './usb UP.png';
import mainAgripeuse from './mainAgripeuse.png';
import bouttonbot from './boutton/bot.png';
import bouttonlecture from './boutton/lecture.png';
import bouttonpause from './boutton/pause.png';
import bouttontop from './boutton/top.png';
import bouttonleft from './boutton/left.png';
import bouttonright from './boutton/right.png';
import tvNoise from './tvNoise.jpg';
import './App.css';

function App() {
  const styleUsb = { width: '8%', position: 'fixed', top: '40vw', left: '75%', zIndex: '1' };
  const mainAgripeuseStyle = { width: '20%', position: 'fixed', top: '37vw', left: '78%', zIndex: '1' };
  const [spinning, setSpinning] = useState(false);
  const [chapitrePoited, setChapitrePoited] = useState(0);
  const [scrollDirect, setscrollDirect] = useState(0);

  function myRepeatingFunction() {
    console.log("Cette fonction se répète toutes les 1 seconde.");
    const container = document.querySelector('.inageConteneur');
    if (container) {
      const scrollAmount = 200; // Vous pouvez ajuster la quantité de défilement
        container.scrollTop += scrollAmount * scrollDirect;
    }
  }

  // Appeler la fonction toutes les 1 seconde
  const interval = setInterval(myRepeatingFunction, 1000); // 1000 ms = 1 seconde

  // Pour arrêter la répétition après un certain temps (par exemple, 10 secondes), vous pouvez utiliser setTimeout pour effacer l'intervalle.
  setTimeout(() => {
    clearInterval(interval);
    console.log("La répétition a été arrêtée après 10 secondes.");
  }, 10000); // 10000 ms = 10 secondes

  const handleButtonClickUsb = () => {
    setSpinning(true);

    // Remettez l'animation à false après quelques secondes pour qu'elle puisse être déclenchée à nouveau
    setTimeout(() => {
      setSpinning(false);
    }, 4000); // Vous pouvez ajuster la durée de l'animation ici
  };

  const scrollContainer = (direction) => {
    const container = document.querySelector('.inageConteneur');
    if (container) {
      const scrollAmount = 200; // Vous pouvez ajuster la quantité de défilement
      if (direction === 'up') {
        setscrollDirect(1)
      } else if (direction === 'down') {
        setscrollDirect(-1)
      }
    }
  };

  const nextChapitre = (valueIncre) => {
    if (valueIncre === '+' && chapitrePoited < chapitreList.length - 1) {
      setChapitrePoited(chapitrePoited + 1)
      handleButtonClickUsb()
    }
    if (valueIncre === '-' && chapitrePoited > 0) {
      setChapitrePoited(chapitrePoited - 1)
      handleButtonClickUsb()
    }
  };

  const chapitreList = [<div>
    <img style={{width: '100%'}} src='Theo.jpeg'></img>
  </div>, <p>caca</p>]



  return (
    <div style={{ backgroundColor: 'black', height: '100vh', display: 'flex', justifyContent: 'center' }}>
      <div className='inageConteneur' style={{ height: '11vw', width: '30vw', backgroundColor: 'red', top: '33vw', position: 'fixed', zIndex: '2', overflow: 'scroll' }}>
      { !spinning ? chapitreList[chapitrePoited] : <img src={tvNoise} style={{width: '100%', height: '100%'}}></img>}
      </div>
      <img style={{ width: '90%', position: 'fixed', zIndex: '0' }} src={tvJudeFond}></img>
      <div>
        <img className={`${spinning ? 'slide-right' : ''}`} style={mainAgripeuseStyle} src={mainAgripeuse}></img>
        <img className={`${spinning ? 'slide-right' : ''}`} style={styleUsb} src={usbDown}></img>
        { !spinning &&
          <img className={`${spinning ? 'slide-right' : ''}`} style={styleUsb} src={usbUp}></img>
        }
      </div>
      <img style={{ width: '90%', position: 'fixed', zIndex: '3' }} src={tvJude}></img>
      <img onClick={() => scrollContainer('up')} style={{zIndex: '1000', height: '3vw', top: '33vw', position: 'fixed', left: '30vw'}} src={bouttontop}></img>
      <img onClick={() => scrollContainer('down')} style={{zIndex: '1000', height: '3vw', top: '39vw', position: 'fixed', left: '30vw'}} src={bouttonbot} ></img>
      <img style={{zIndex: '1000', height: '3vw', top: '36vw', position: 'fixed', left: '30vw'}} src={bouttonpause}></img>
      <img onClick={() => nextChapitre('+')} style={{zIndex: '1000', height: '3vw', top: '40vw', position: 'fixed', left: '74vw'}} src={bouttonright}></img>
      <img onClick={() => nextChapitre('-')} style={{zIndex: '1000', height: '3vw', top: '40vw', position: 'fixed', left: '69vw'}} src={bouttonleft}></img>
      <img style={{zIndex: '1000', height: '3vw', top: '40vw', position: 'fixed', left: '71vw'}} src={bouttonlecture}></img>
    </div>
  );
}

export default App;
