import React, { useEffect, useState, useRef} from 'react';
import tvJude from './TV Jude.png';
import tvJudeFond from './TV Jude cheveux derier.png';
import usbDown from './usb down.png';
import usbUp from './usb UP.png';
import mainAgripeuse from './mainAgripeuse.png';
import bouttonbot from './boutton/bot.png';
import bouttonlecture from './boutton/lecture.png';
import bouttonpause from './boutton/pause.png';
import bouttontop from './boutton/top.png';
import bouttonleft from './boutton/left.png';
import bouttonright from './boutton/right.png';
import tvNoise from './tvNoise.jpg';
import './App.css';

function App() {
    const styleUsb = { width: '8%', position: 'fixed', top: '40vw', left: '75%', zIndex: '1' };
    const mainAgripeuseStyle = { width: '20%', position: 'fixed', top: '37vw', left: '78%', zIndex: '1' };
    const [spinning, setSpinning] = useState(false);
    const [chapitrePoited, setChapitrePoited] = useState(0);
    const [scrolling, setScrolling] = useState(0);
    const containerRef = useRef(null);

    const handleButtonClickUsb = () => {
      setSpinning(true);

      // Remettez l'animation à false après quelques secondes pour qu'elle puisse être déclenchée à nouveau
      setTimeout(() => {
        setSpinning(false);
      }, 4000); // Vous pouvez ajuster la durée de l'animation ici
    };

    const scrollContainer = () => {
      const container = containerRef.current;
      if (container) {
        if (scrolling === 1) {
          container.scrollTop += 1; // Défilement vers le bas
        } else if (scrolling === -1) {
          container.scrollTop -= 1; // Défilement vers le haut
        }
      }
    };

    const nextChapitre = (valueIncre) => {
      if (valueIncre === '+' && chapitrePoited < chapitreList.length - 1) {
        setChapitrePoited(chapitrePoited + 1);
        handleButtonClickUsb();
      }
      if (valueIncre === '-' && chapitrePoited > 0) {
        setChapitrePoited(chapitrePoited - 1);
        handleButtonClickUsb();
      }
    };

    const chapitreList = [<div>
      <img style={{ width: '100%' }} src='Theo.jpeg'></img>
    </div>, <p>caca</p>];

    useEffect(() => {
      const container = containerRef.current;
      const scrollInterval = setInterval(() => {
        scrollContainer();
      }, 20); // 20 ms de délai entre chaque défilement
      return () => {
        clearInterval(scrollInterval);
      };
    }, [scrolling]);

    return (
      <div style={{ backgroundColor: 'black', height: '100vh', display: 'flex', justifyContent: 'center' }}>
        <div ref={containerRef} className='inageConteneur' style={{ height: '11vw', width: '30vw', backgroundColor: 'red', top: '33vw', position: 'fixed', zIndex: '2', overflow: 'scroll' }}>
          {!spinning ? chapitreList[chapitrePoited] : <img src={tvNoise} style={{ width: '100%', height: '100%' }} alt="TV Noise" />}
        </div>
        <img style={{ width: '90%', position: 'fixed', zIndex: '0' }} src={tvJudeFond} alt="TV Jude Fond" />
        <div>
          <img className={`${spinning ? 'slide-right' : ''}`} style={mainAgripeuseStyle} src={mainAgripeuse} alt="Main Agripeuse" />
          <img className={`${spinning ? 'slide-right' : ''}`} style={styleUsb} src={usbDown} alt="USB Down" />
          {!spinning &&
            <img className={`${spinning ? 'slide-right' : ''}`} style={styleUsb} src={usbUp} alt="USB Up" />
          }
        </div>
        <img onClick={() => setScrolling(-1)} style={{ zIndex: '1000', height: '3vw', top: '33vw', position: 'fixed', left: '30vw' }} alt="Bouton Top" src={bouttontop} />
        <img onClick={() => setScrolling(1)} style={{ zIndex: '1000', height: '3vw', top: '39vw', position: 'fixed', left: '30vw' }} alt="Bouton Bot" src={bouttonbot} />
        <img onClick={() => setScrolling(0)} style={{ zIndex: '1000', height: '3vw', top: '36vw', position: 'fixed', left: '30vw' }} alt="Bouton Pause" src={bouttonpause} />
        <img onClick={() => nextChapitre('+')} style={{ zIndex: '1000', height: '3vw', top: '40vw', position: 'fixed', left: '74vw' }} alt="Bouton Right" src={bouttonright} />
        <img onClick={() => nextChapitre('-')} style={{ zIndex: '1000', height: '3vw', top: '40vw', position: 'fixed', left: '69vw' }} alt="Bouton Left" src={bouttonleft} />
        <img style={{ zIndex: '1000', height: '3vw', top: '40vw', position: 'fixed', left: '71vw' }} alt="Bouton Lecture" src={bouttonlecture} />
      </div>
    );
  }

export default App;
